// LeaderBoard.js
import React, { useState, useEffect } from 'react';

function LeaderBoard({ points, userName }) {
  const [leaderBoardData, setLeaderBoardData] = useState([]);

  useEffect(() => {
    // Assuming leaderBoardData is an array of objects containing username and points
    const leaderBoard = [
      { username: 'User1', points: 100 },
      { username: 'User2', points: 90 },
      // Add more data here
    ];

    // Sort the leaderBoardData based on points in descending order
    leaderBoard.sort((a, b) => b.points - a.points);

    setLeaderBoardData(leaderBoard);
  }, []);

  const getUserRank = () => {
    // Find the index of the current user in the sorted leaderBoardData array
    const userIndex = leaderBoardData.findIndex(user => user.username === userName);

    if (userIndex !== -1) {
      // User found in the top 10
      return userIndex + 1; // Rank starts from 1, not 0
    } else {
      // User not found in the top 10, display their rank as '-'
      return '-';
    }
  };

  return (
    <div className="leaderboard">
      <h2>Leaderboard</h2>
      <table>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Username</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {leaderBoardData.slice(0, 10).map((user, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{user.username}</td>
              <td>{user.points}</td>
            </tr>
          ))}
          <tr>
            <td>{getUserRank()}</td>
            <td>{userName}</td>
            <td>{points}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default LeaderBoard;
