// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';
import axios from 'axios';

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [userPoints, setUserPoints] = useState(0);
  const [deckSize, setDeckSize] = useState(5);
  const [defuseCounter, setDefuseCounter] = useState(0);

  useEffect(() => {
    // Fetch user points when the component mounts
    const fetchUserPoints = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/user/points?name=${userName}`);
        setUserPoints(response.data);
      } catch (error) {
        console.error('Error fetching user points:', error);
      }
    };

    if (userName) {
      fetchUserPoints();
    }
  }, [userName]);

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true); // Set gameStarted to true once the user submits the name
  };

  const handleCardFlip = (cardType) => {
    switch (cardType) {
      case 'cat':
        setDeckSize(deckSize - 1);
        break;
      case 'defuse':
        setDeckSize(deckSize - 1);
        setDefuseCounter(defuseCounter + 1);
        break;
      case 'exploding':
        if (defuseCounter > 0) {
          setDefuseCounter(defuseCounter - 1);
        } else {
          setDeckSize(deckSize - 1);
          setToastMessage('Game Over! You drew an exploding kitten.');
          return; // Exit the function to prevent further execution
        }
        break;
      case 'shuffle':
        setDeckSize(5);
        setDefuseCounter(0);
        break;
      default:
        break;
    }

    // Check if all cards in the deck are exhausted
    if (deckSize === 0) {
      // Update user points in the database
      updateUserPoints(points + 1);
      setToastMessage('Congratulations! You won the game.');
    }
  };

  const updateUserPoints = async (newPoints) => {
    try {
      await axios.put('http://localhost:8080/api/user/points', { name: userName, points: newPoints });
      setUserPoints(newPoints);
    } catch (error) {
      console.error('Error updating user points:', error);
    }
  };

  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar userName={userName} userPoints={userPoints} />
          <div className="card-container">
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
          </div>
          {toastMessage && <Toast message={toastMessage} />}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;
