import React, { useState, useEffect } from 'react';

function LeaderBoard() {
  const [leaderBoardData, setLeaderBoardData] = useState([]);

  useEffect(() => {
    fetchLeaderboard(); // Fetch leaderboard data when component mounts
  }, []);

  const fetchLeaderboard = () => {
    fetch('http://localhost:8080/api/leaderboard')
      .then(response => response.json())
      .then(data => setLeaderBoardData(data))
      .catch(error => console.error('Error fetching leaderboard data:', error));
  };

  const handleNewGame = () => {
    window.location.reload();
  };

  // Separate users into two arrays based on rank
  const topFiveUsers = leaderBoardData.slice(0, 5);
  const otherUsers = leaderBoardData.slice(5);

  return (
    <div className="leaderboard-container">
      <h2>Leader Board</h2>
      <div className="scrollable-leaderboard">
        <table className="leaderboard-table">
          <thead>
            <tr>
              <th>Rank</th>
              <th>Username</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {/* Display top 5 users with fixed positioning */}
            {topFiveUsers.map((user, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{user.username}</td>
                <td>{user.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {/* Display remaining users with scrollable positioning */}
        <div className="scrollable-users">
          <table className="leaderboard-table">
            <tbody>
              {otherUsers.map((user, index) => (
                <tr key={index}>
                  <td>{index + 6}</td> {/* Start rank from 6 for other users */}
                  <td>{user.username}</td>
                  <td>{user.points}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <div style={{ textAlign: 'center' }}>
        <button
          type="button"
          onClick={handleNewGame}
          style={{
            backgroundColor: 'red',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
            fontWeight: 'bolder',

          }}
        >
          New Game
        </button>
      </div>
    </div>
  );
}

export default LeaderBoard;
