// App.js
import React, { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import WelcomePage from './components/WelcomePage'; // Import the WelcomePage component
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Import BrowserRouter and Route

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');

  const handleCardFlip = (cardType) => {
    // Logic for handling card flip and updating points
    switch (cardType) {
      case 'defuse':
        setPoints(points + 1);
        setToastMessage('You gained a point!');
        break;
      case 'exploding':
        setPoints(0);
        setToastMessage('Game Over! You drew an exploding kitten.');
        break;
      // Add more cases for other card types as needed
      default:
        setToastMessage('Card drawn successfully!');
        break;
    }
  };

  return (
    <Router>
      <div className="App">
        <Navbar points={points} />
        <Switch>
          <Route exact path="/">
            <WelcomePage />
          </Route>
          <Route path="/game">
            <div>
              <div className="card-container">
                <CardBlock onCardFlip={handleCardFlip} />
                <CardBlock onCardFlip={handleCardFlip} />
                <CardBlock onCardFlip={handleCardFlip} />
                <CardBlock onCardFlip={handleCardFlip} />
                <CardBlock onCardFlip={handleCardFlip} />
              </div>
              {toastMessage && <Toast message={toastMessage} />}
            </div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
