// CardBlock.js
import React, { useState } from 'react';
import cat1 from '../assets/cat1.png';
import cat2 from '../assets/cat2.png';
import cat3 from '../assets/cat3.png';
import cat4 from '../assets/cat4.png';

function CardBlock({ onCardFlip }) {
  const [flipped, setFlipped] = useState(false);
  const [cardType, setCardType] = useState('');

  const handleClick = () => {
    if (!flipped) {
      setFlipped(true);
      // Generate random card type (for demonstration purpose)
      const cardTypes = ['cat', 'defuse', 'exploding', 'shuffle'];
      const randomCardType = cardTypes[Math.floor(Math.random() * cardTypes.length)];
      setCardType(randomCardType);
      onCardFlip(randomCardType);
    }
  };

  const getBackImage = () => {
    switch (cardType) {
      case 'cat':
        return cat1;
      case 'defuse':
        return cat2;
      case 'shuffle':
        return cat3;
      case 'exploding':
        return cat4;
      default:
        return '';
    }
  };

  return (
    // <div className={`card-block ${flipped ? 'flipped' : ''}`} onClick={handleClick}>
    <div className={`card-block ${isFlipped ? 'hidden' : ''}`} onClick={handleClick}>
      <div className="card-front"></div>
      <div className="card-back" style={{ backgroundImage: `url(${getBackImage()})`, backgroundSize: '80%', backgroundRepeat: 'no-repeat', backgroundPosition: 'center' }}></div>
    </div>
  );
}

export default CardBlock;
