import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';
import LeaderBoard from './components/LeaderBoard'; // Import LeaderBoard component

function App() {
  const [points, setPoints] = useState(0);
  const [count, setCount] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [cards, setCards] = useState([]);
  const [flippedArray, setFlippedArray] = useState([]);
  const [allCardsDrawn, setAllCardsDrawn] = useState(false); // State variable to track if all cards are drawn

useEffect(() => {
    if (gameStarted) {
      generateCards();
      fetchUserPoints(); // Fetch user points when the game starts
    }
}, [gameStarted, fetchUserPoints]); // Include fetchUserPoints in the dependency array


  useEffect(() => {
    setFlippedArray(Array(cards.length).fill(false));
  }, [cards]);

  const generateCards = () => {
    const cardTypes = ['cat', 'defuse', 'exploding', 'shuffle'];
    const newCards = Array.from({ length: 5 }, () => {
      return cardTypes[Math.floor(Math.random() * cardTypes.length)];
    });
    setCards(newCards);
  };

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true);
  };

  const handleCardFlip = (index, cardType) => {
    setTimeout(() => {
      switch (cardType) {
        case 'defuse':
          setCount(count + 1);
          setToastMessage('You can defuse an exploding kitten now!');
          break;
        case 'cat':
          setToastMessage('Meow! one step ahead to win!');
          break;
        case 'shuffle':
          setCount(0);
          setToastMessage('All Cards are shuffled, start from the beginning');
          generateCards();
          return;
        case 'exploding':
          if (count > 0) {
            setCount(count - 1);
            setToastMessage('Oops! You defused the bomb, but be cautious!');
          } else {
            setToastMessage('Game Over! You drew an exploding kitten and you have no defuse card.');
            setGameStarted(false);
            window.location.reload();
            // setAllCardsDrawn(true); // Set allCardsDrawn to true
          }
          break;
        default:
          setToastMessage('All Cards drawn successfully!');
          // You can implement database interaction logic here
          break;
      }

      const newCards = [...cards];
      newCards.splice(index, 1);
      setCards(newCards);

      const newFlippedArray = [...flippedArray];
      newFlippedArray[index] = true;
      setFlippedArray(newFlippedArray);

      // Check if all cards are drawn
      if (newCards.length === 0) {
        setAllCardsDrawn(true);
        updateUserPoints(); // Update user points when all cards are drawn
      }
    }, 700);
  };

  const fetchUserPoints = () => {
    fetch(`/api/user/data?name=${userName}`)
        .then(response => response.json())
        .then(data => setPoints(parseInt(data.points))) // Parse points as integer
        .catch(error => console.error('Error fetching user points:', error));
};


    const updateUserPoints = () => {
        const updatedPoints = points + 1; // Increment user points by 1
        fetch('/api/user/points', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ name: userName, points: updatedPoints })
        })
        .then(response => {
            if (response.ok) {
                setPoints(updatedPoints); // Update points in the frontend
            } else {
                console.error('Failed to update user points');
            }
        })
        .catch(error => {
            console.error('Error updating user points:', error);
        });
    };

    return (
      <div className="App">
          {gameStarted ? (
              <>
                  <Navbar points={points} userName={userName}/>
                  <div className="card-container">
                      {cards.map((card, index) => (
                          <CardBlock key={index} cardType={card} index={index} onCardFlip={handleCardFlip} flippedArray={flippedArray} />
                      ))}
                  </div>
                  {toastMessage && <Toast message={toastMessage} />}
              </>
          ) : (
              <>
                  <NameForm onNameSubmit={handleNameSubmit} />
              </>
          )}
          {allCardsDrawn && <LeaderBoard points={points} userName={userName} />} {/* Render LeaderBoard only when all cards are drawn */}
      </div>
  );
}

export default App;
