// LeaderBoard.js
import React from 'react';

function LeaderBoard({ users, currentUser }) {
  // Sort users by points in descending order
  const sortedUsers = [...users].sort((a, b) => b.points - a.points);

  // Get rank of the current user
  const userRank = sortedUsers.findIndex(user => user === currentUser) + 1;

  return (
    <div className="leaderboard">
      <h2>Leaderboard</h2>
      <table>
        <thead>
          <tr>
            <th>Rank</th>
            <th>Name</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {sortedUsers.slice(0, 10).map((user, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{user.name}</td>
              <td>{user.points}</td>
            </tr>
          ))}
          {/* Display user's rank if not in top 10 */}
          {userRank > 10 && (
            <tr>
              <td>{userRank}</td>
              <td>{currentUser.name}</td>
              <td>{currentUser.points}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default LeaderBoard;
