// App.js
import React, { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true); // Set gameStarted to true once the user submits the name
  };

  const handleCardFlip = async (cardType) => {
    try {
      switch (cardType) {
        case 'cat':
          // Remove one cat card from the deck
          await axios.delete('http://localhost:8080/api/deck/cat');
          break;
        case 'defuse':
          // Remove one defuse card from the deck
          await axios.delete('http://localhost:8080/api/deck/defuse');
          // Increment the counter
          setCounter((prevCounter) => prevCounter + 1);
          break;
        case 'exploding':
          // If counter is greater than 0, decrement the counter and continue the game
          if (counter > 0) {
            setCounter((prevCounter) => prevCounter - 1);
          } else {
            // End the game if counter is 0
            setGameEnded(true);
            setToastMessage('Game Over! You drew an exploding kitten.');
            return;
          }
          break;
        case 'shuffle':
          // Reset the deck to 5 cards and counter to 0
          await axios.put('http://localhost:8080/api/deck/shuffle');
          setCounter(0);
          break;
        default:
          break;
      }

      // If all cards are removed from the deck, increase user points
      const remainingCards = await axios.get('http://localhost:8080/api/deck/count');
      if (remainingCards.data === 0) {
        // Increment user points in the database
        await axios.put(`http://localhost:8080/api/user/points?name=${userName}`);
      }

      // Display toast message
      setToastMessage('Card drawn successfully!');
    } catch (error) {
      console.error('Error handling card flip:', error);
      setToastMessage('Failed to handle card flip. Please try again.');
    }
  };


  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar points={points} />
          <div className="card-container">
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
          </div>
          {toastMessage && <Toast message={toastMessage} />}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;
