// Toast.js
import React, { useEffect } from 'react';

function Toast({ message }) {
  useEffect(() => {
    const toast = document.querySelector('.toast');
    toast.classList.add('show');
    setTimeout(() => {
      toast.classList.remove('show');
    }, 2000); // Adjust the time as needed
  }, [message]);

  return (
    <div className="toast">
      <p>{message}</p>
    </div>
  );
}

export default Toast;
