// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    if (gameStarted) {
      generateCards();
    }
  }, [gameStarted]);

  const generateCards = () => {
    const cardTypes = ['cat', 'defuse', 'exploding', 'shuffle'];
    const newCards = Array.from({ length: 5 }, () => {
      return cardTypes[Math.floor(Math.random() * cardTypes.length)];
    });
    setCards(newCards);
  };

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true);
  };

  const handleCardFlip = (index, cardType) => {
    const newCards = [...cards];
    // Remove the selected card from the array
    newCards.splice(index, 1);
    setCards(newCards);

    switch (cardType) {
      case 'defuse':
        setPoints(points + 1);
        break;
      case 'exploding':
        setPoints(0);
        setToastMessage('Game Over! You drew an exploding kitten.');
        break;
      case 'shuffle':
        generateCards(); // Regenerate the cards
        setToastMessage('Deck shuffled!');
        break;
      default:
        // Cat card drawn, no additional action required
        break;
    }
  };

  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar points={points} userName={userName}/>
          <div className="card-container">
            {cards.map((card, index) => (
              <CardBlock key={index} cardType={card} index={index} onCardFlip={handleCardFlip} />
            ))}
          </div>
          {toastMessage && <Toast message={toastMessage} />}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;
