import React, { useState } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [c, setC] = useState(0); // Initialize counter 'c'
  const [numOfExplodeCards] = useState(2); // Initialize number of explode cards

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true); // Set gameStarted to true once the user submits the name
  };

  const handleCardFlip = (cardType) => {
    // Logic for handling card flip and updating points
    switch (cardType) {
      case 'cat':
        setPoints(points + 1);
        setToastMessage('You gained a point!');
        // Remove cat card from deck after a delay
        setTimeout(() => {
          // Logic to remove card from deck
        }, 1000); // Delay in milliseconds
        break;
      case 'defuse':
        setPoints(points + 1);
        setToastMessage('You gained a point!');
        // Increment counter 'c'
        setC(c + 1);
        // Remove defuse card from deck after a delay
        setTimeout(() => {
          // Logic to remove card from deck
        }, 1000); // Delay in milliseconds
        break;
      case 'exploding':
        // Check if counter 'c' is greater than or equal to the number of explode cards
        if (c >= numOfExplodeCards) {
          setPoints(points + 1);
          setToastMessage('You defused the bomb!');
          // Remove exploding card from deck after a delay
          setTimeout(() => {
            // Logic to remove card from deck
          }, 1000); // Delay in milliseconds
        } else {
          setPoints(0);
          setToastMessage('Game Over! You drew an exploding kitten.');
        }
        break;
      case 'shuffle':
        setPoints(points + 1);
        setToastMessage('Game restarted!');
        // Reset deck and counter 'c'
        setC(0);
        break;
      default:
        setToastMessage('Card drawn successfully!');
        break;
    }
  };

  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar points={points} userName={userName} />
          <div className="card-container">
            <CardBlock onCardFlip={handleCardFlip} />
            <CardBlock onCardFlip={handleCardFlip} />
            <CardBlock onCardFlip={handleCardFlip} />
            <CardBlock onCardFlip={handleCardFlip} />
            <CardBlock onCardFlip={handleCardFlip} />
          </div>
          {toastMessage && <Toast message={toastMessage} />}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;
