// NameForm.js
import React, { useState } from 'react';
import axios from 'axios';

function NameForm({ onNameSubmit }) {
  const [name, setName] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/user', { name });
      onNameSubmit(name); // Call the onNameSubmit function passed from App.js
    } catch (error) {
      console.error('Error submitting name:', error);
      alert('Failed to submit name. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ textAlign: 'center' }}>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter Your Name"
        style={{
          backgroundColor: 'transparent',
          border: '1px solid red',
          color: 'red',
          padding: '8px',
          borderRadius: '5px',
          marginBottom: '10px',
          width: '200px', // Set width as needed
          outline: 'none', // Remove default focus outline
        }}
      />
      <br />
      <button
        type="submit"
        style={{
          backgroundColor: 'red',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          fontSize: '16px',
          fontWeight: 'bolder'
        }}
      >
        Submit
      </button>
    </form>
  );
}

export default NameForm;
