// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';
import LeaderBoard from './components/LeaderBoard';


function App() {
  const [points, setPoints] = useState(0);
  const [count, setCount] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [cards, setCards] = useState([]);
  const [flippedArray, setFlippedArray] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    if (gameStarted) {
      generateCards();
    }
  }, [gameStarted]);

  useEffect(() => {
    // Initialize flipped state for all cards
    setFlippedArray(Array(cards.length).fill(false));
  }, [cards]);

  const generateCards = () => {
    const cardTypes = ['cat', 'defuse', 'exploding', 'shuffle'];
    const newCards = Array.from({ length: 5 }, () => {
      return cardTypes[Math.floor(Math.random() * cardTypes.length)];
    });
    setCards(newCards);
  };

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true);
  };

  const handleCardFlip = (index, cardType) => {
    setTimeout(() => {
      switch (cardType) {
        case 'defuse':
          setCount(count + 1);
          setToastMessage('You can defuse an exploding kitten now!');
          console.log("Count is" +count);
          break;
        case 'cat':
          setToastMessage('Meow! one step ahead to win!');
          break;
        case 'shuffle':
          // {
          //   const cardTypes = ['cat', 'defuse', 'exploding', 'shuffle'];
          //   const newCards = Array.from({ length: 5 }, () => {
          //     return cardTypes[Math.floor(Math.random() * cardTypes.length)];
          //   });
          //   setCards(newCards);
          // }; 
          setToastMessage('All Cards are shuffled, start from the beginning');
          break;
        case 'exploding':
          if (count > 0) {
            setCount(count - 1);
            console.log("Count is" +count);
            setToastMessage('Oops! You defused the bomb, but be cautious!');
          } else {
            setToastMessage('Game Over! You drew an exploding kitten and you have no defuse card.');
            setGameStarted(false); // Exit the game
          }
          break;
        default:
          setToastMessage('All Cards drawn successfully!');
          // Push a point to the database against the particular username
          // You can implement this logic as per your database structure
          break;
      }

      const newCards = [...cards];
      newCards.splice(index, 1); // Remove the card from the array
      setCards(newCards);

      const newFlippedArray = [...flippedArray];
      newFlippedArray[index] = true; // Flip the removed card
      setFlippedArray(newFlippedArray);
    }, 700); // Wait for 0.7 seconds before removing the card
  };


  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar points={points} userName={userName}/>
          <div className="card-container">
            {cards.map((card, index) => (
              <CardBlock key={index} cardType={card} index={index} onCardFlip={handleCardFlip} flippedArray={flippedArray} />
            ))}
          </div>
          {toastMessage && <Toast message={toastMessage} />}
          {/* Display LeaderBoard when all cards are drawn */}
          {points === 5 && <LeaderBoard users={users} currentUser={currentUser} />}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;



