// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [cards, setCards] = useState([]);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    if (gameStarted) {
      generateCards();
    }
  }, [gameStarted]);

  const generateCards = () => {
    const cardTypes = ['cat', 'defuse', 'exploding', 'shuffle'];
    const newCards = Array.from({ length: 5 }, () => {
      return cardTypes[Math.floor(Math.random() * cardTypes.length)];
    });
    setCards(newCards);
  };

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true);
    setGameOver(false);
    setPoints(0);
    setToastMessage('');
  };

  const handleCardFlip = (index, cardType) => {
    // Logic for handling card flip and updating points
    if (cardType === 'exploding') {
      setGameOver(true);
      setToastMessage('Game Over! You drew an exploding kitten.');
      return;
    }

    setTimeout(() => {
      const newCards = [...cards];
      newCards.splice(index, 1); // Remove the card from the array
      setCards(newCards);

      switch (cardType) {
        case 'cat':
          break;
        case 'defuse':
          setToastMessage('You defused a bomb!');
          break;
        case 'shuffle':
          setToastMessage('Shuffling the deck!');
          generateCards();
          break;
        default:
          break;
      }
    }, 3000); // Wait for 3 seconds before removing the card

    switch (cardType) {
      case 'defuse':
        setPoints(points + 1);
        break;
      default:
        break;
    }
  };

  const handleGameRestart = () => {
    setGameStarted(false);
    setPoints(0);
    setToastMessage('');
    setUserName('');
    setGameOver(false);
  };

  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar points={points} userName={userName}/>
          <div className="card-container">
            {cards.map((card, index) => (
              <CardBlock key={index} cardType={card} index={index} onCardFlip={handleCardFlip} />
            ))}
          </div>
          {toastMessage && <Toast message={toastMessage} />}
          {gameOver && <button onClick={handleGameRestart}>Restart Game</button>}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;
