// LeaderBoard.js
import React, { useState, useEffect } from 'react';

function LeaderBoard() {
    const [leaderBoardData, setLeaderBoardData] = useState([]);

    useEffect(() => {
      // Fetch leaderboard data from the backend API
      fetch('/api/leaderboard')
        .then(response => response.json())
        .then(data => {
          setLeaderBoardData(data);
        })
        .catch(error => console.error('Error fetching leaderboard data:', error));
    }, []);

  const getUserRank = () => {
    // Find the index of the current user in the sorted leaderBoardData array
    const userIndex = leaderBoardData.findIndex(user => user.username === userName);

    if (userIndex !== -1) {
      // User found in the top 10
      return userIndex + 1; // Rank starts from 1, not 0
    } else {
      // User not found in the top 10, display their rank as '-'
      return '-';
    }
  };

  const handleNewGame = () => {
    window.location.reload();
  };

  return (
    <div className="leaderboard-container">
      <h2>Leader Board</h2>
      <table className="leaderboard-table">
        <thead>
          <tr>
            <th>Rank</th>
            <th>Username</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {leaderBoardData.slice(0, 10).map((user, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{user.username}</td>
              <td>{user.points}</td>
            </tr>
          ))}
          <tr>
            <td>{getUserRank()}</td>
            <td>{userName}</td>
            <td>{points}</td>
          </tr>
        </tbody>
      </table>
      <div style={{ textAlign: 'center' }}>
        <button
          type="button"
          onClick={handleNewGame}
          style={{
            backgroundColor: 'red',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '16px',
            fontWeight: 'bolder'
          }}
        >
          New Game
        </button>
      </div>
    </div>
  );
}

export default LeaderBoard;
