// App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import CardBlock from './components/CardBlock';
import Toast from './components/Toast';
import NameForm from './components/NameForm';
import axios from 'axios';

function App() {
  const [points, setPoints] = useState(0);
  const [toastMessage, setToastMessage] = useState('');
  const [userName, setUserName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [userPoints, setUserPoints] = useState(0);

  useEffect(() => {
    // Fetch user points when the component mounts
    const fetchUserPoints = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/user/points?name=${userName}`);
        setUserPoints(response.data);
      } catch (error) {
        console.error('Error fetching user points:', error);
      }
    };

    if (userName) {
      fetchUserPoints();
    }
  }, [userName]);

  const handleNameSubmit = (name) => {
    setUserName(name);
    setGameStarted(true); // Set gameStarted to true once the user submits the name
  };

  const handleCardFlip = (cardType) => {
  // Logic for handling card flip and updating points
  switch (cardType) {
    case 'cat':
      if (deck.length > 0) {
        setDeck(deck.slice(0, -1));
      }
      break;
    case 'defuse':
      if (deck.length > 0) {
        setDeck(deck.slice(0, -1));
        setCounter(counter + 1); // Increment counter
      }
      break;
    case 'exploding':
      if (counter > 0) {
        setCounter(counter - 1); // Decrement counter
      } else {
        setGameStarted(false); // End game if counter is 0
        setPoints(0);
        setToastMessage('Game Over! You drew an exploding kitten.');
      }
      if (deck.length > 0) {
        setDeck(deck.slice(0, -1));
      }
      break;
    case 'shuffle':
      setDeck(Array.from({ length: 5 }, (_, index) => index + 1));
      setCounter(0);
      break;
    default:
      break;
  }
};


  return (
    <div className="App">
      {gameStarted ? (
        <>
          <Navbar userName={userName} userPoints={userPoints} />
          <div className="card-container">
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
            <CardBlock userName={userName} onCardFlip={handleCardFlip} />
          </div>
          {toastMessage && <Toast message={toastMessage} />}
        </>
      ) : (
        <NameForm onNameSubmit={handleNameSubmit} />
      )}
    </div>
  );
}

export default App;
